function initMap(){var e="Barbate",o=new google.maps.Geocoder,t=new google.maps.Map(document.getElementById("map"),{scrollwheel:!1,zoom:15,mapTypeControl:!1});o.geocode({address:e},function(o,a){if(a==google.maps.GeocoderStatus.OK){t.setCenter(o[0].geometry.location);var n=new google.maps.Marker({map:t,title:e,position:o[0].geometry.location})}else alert("Geocode was not successful for the following reason: "+a)})}